import React from 'react'
import Background from './Background'
import Header from './Header'
import Responsivo from './Responsivo'

const Home = () => {
    const [ativo, setAtivo] = React.useState(false)

    function handleClick() {
        setAtivo(!ativo)
    }

    return <div>
        <Header event={handleClick} />
        {ativo && <Responsivo />}
        <Background />
    </div>
}

export default Home
