import { Dropdown } from 'bootstrap'
import React from 'react'
import Header from './Components/Header'
import './Css/Global.css'

const App = () => {
    const [active, setActive] = React.useState(false)

    function handleClick() {
        setActive(!active)
    }

    return <div>
        <Header onClick={handleClick} />
        {active && <Dropdown />}
    </div>
}

export default App
